CREATE EXTENSION IF NOT EXISTS "pgcrypto";

CREATE TABLE IF NOT EXISTS catalog_meta (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  generated_at TIMESTAMPTZ NOT NULL,
  assumptions JSONB NOT NULL,
  upgrade_templates JSONB NOT NULL
);

CREATE TABLE IF NOT EXISTS sectors (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  sector_code TEXT NOT NULL UNIQUE,
  name TEXT NOT NULL,
  description TEXT NOT NULL,
  decision_profile TEXT NOT NULL,
  upgrade_profile TEXT NOT NULL,
  startup_cost_min_eur NUMERIC(14,2) NOT NULL,
  startup_cost_max_eur NUMERIC(14,2) NOT NULL,
  startup_cost_avg_eur NUMERIC(14,2) NOT NULL,
  startup_cost_median_eur NUMERIC(14,2) NOT NULL,
  CHECK (upgrade_profile IN ('DIGITAL','INDUSTRIAL','REGULATED','SERVICE'))
);

CREATE TABLE IF NOT EXISTS niches (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  sector_id UUID NOT NULL REFERENCES sectors(id) ON DELETE CASCADE,
  niche_code TEXT NOT NULL,
  name TEXT NOT NULL,
  startup_cost_eur NUMERIC(14,2) NOT NULL,
  roi_pct NUMERIC(5,2) NOT NULL,
  payback_years NUMERIC(6,2) NOT NULL,
  risk TEXT NOT NULL,
  capex TEXT NOT NULL,
  margin_pct_min NUMERIC(5,2) NOT NULL,
  margin_pct_max NUMERIC(5,2) NOT NULL,
  base_demand_index INT NOT NULL,
  ticket_level TEXT NOT NULL,
  competition TEXT NOT NULL,
  decision_profile TEXT NOT NULL,
  upgrade_profile TEXT NOT NULL,
  pricing_model TEXT NOT NULL,
  volume_baseline_week_min NUMERIC(14,2) NOT NULL,
  volume_baseline_week_max NUMERIC(14,2) NOT NULL,
  volume_unit TEXT NOT NULL,
  fixed_costs_month_min_eur NUMERIC(14,2) NOT NULL,
  fixed_costs_month_max_eur NUMERIC(14,2) NOT NULL,
  working_capital_days JSONB NOT NULL,
  maintenance_pct_of_capex_per_year_min NUMERIC(5,2) NOT NULL,
  maintenance_pct_of_capex_per_year_max NUMERIC(5,2) NOT NULL,
  CHECK (risk IN ('Low','Medium','High'))
  CHECK (capex IN ('LOW','MEDIUM','HIGH'))
  CHECK (ticket_level IN ('LOW','MEDIUM','HIGH'))
  CHECK (competition IN ('FRAGMENTED','OLIGOPOLY','MONOPOLY_LIKE'))
  CHECK (pricing_model IN ('subscription','usage','one_off','mixed'))
  CHECK (upgrade_profile IN ('DIGITAL','INDUSTRIAL','REGULATED','SERVICE'))
  CHECK (volume_unit IN ('per_month','per_order','per_ton','per_km','per_hour','per_day','per_night','per_project','per_batch','per_unit','percent'))
  UNIQUE (sector_id, niche_code)
);

CREATE TABLE IF NOT EXISTS niche_products (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  niche_id UUID NOT NULL REFERENCES niches(id) ON DELETE CASCADE,
  sku TEXT NOT NULL,
  name TEXT NOT NULL,
  unit TEXT NOT NULL,
  price_min_eur NUMERIC(14,2) NOT NULL,
  price_max_eur NUMERIC(14,2) NOT NULL,
  cogs_pct_min NUMERIC(5,2) NOT NULL,
  cogs_pct_max NUMERIC(5,2) NOT NULL,
  capacity_driver TEXT NOT NULL,
  notes TEXT NOT NULL,
  CHECK (unit IN ('per_month','per_order','per_ton','per_km','per_hour','per_day','per_night','per_project','per_batch','per_unit','percent'))
  CHECK (capacity_driver IN ('FTE','assets','bays','beds','trucks','machines','pallets','seats','sqm'))
  CHECK (price_min_eur < price_max_eur),
  CHECK (cogs_pct_min >= 0 AND cogs_pct_max <= 100 AND cogs_pct_min <= cogs_pct_max)
);

CREATE TABLE IF NOT EXISTS upgrade_profiles (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  profile_code TEXT NOT NULL,
  branch_code TEXT NOT NULL,
  name TEXT NOT NULL,
  description TEXT NOT NULL,
  effects JSONB NOT NULL,
  risk_notes TEXT NOT NULL,
  CHECK (profile_code IN ('DIGITAL','INDUSTRIAL','REGULATED','SERVICE'))
  UNIQUE (profile_code, branch_code)
);

CREATE TABLE IF NOT EXISTS upgrade_programs (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  niche_id UUID NOT NULL REFERENCES niches(id) ON DELETE CASCADE,
  upgrade_profile_id UUID NOT NULL REFERENCES upgrade_profiles(id) ON DELETE CASCADE,
  profile_code TEXT NOT NULL,
  branch_code TEXT NOT NULL,
  tier TEXT NOT NULL,
  capex_formula TEXT NOT NULL,
  opex_formula TEXT NOT NULL,
  effects JSONB NOT NULL,
  delay_weeks_min INT NOT NULL,
  delay_weeks_max INT NOT NULL,
  risk JSONB NOT NULL,
  CHECK (tier IN ('T1','T2','T3')),
  CHECK (profile_code IN ('DIGITAL','INDUSTRIAL','REGULATED','SERVICE'))
);







-- Sanity checks
SELECT n.id, n.name, COUNT(p.id) AS product_count
FROM niches n
LEFT JOIN niche_products p ON p.niche_id = n.id
GROUP BY n.id, n.name
HAVING COUNT(p.id) < 3;

SELECT n.id, n.name, COUNT(up.id) AS program_count
FROM niches n
LEFT JOIN upgrade_programs up ON up.niche_id = n.id
GROUP BY n.id, n.name
HAVING COUNT(up.id) < 9;

SELECT n.id, n.name, up.branch_code, COUNT(DISTINCT up.tier) AS tiers
FROM niches n
JOIN upgrade_programs up ON up.niche_id = n.id
GROUP BY n.id, n.name, up.branch_code
HAVING COUNT(DISTINCT up.tier) < 3;

SELECT * FROM niche_products WHERE price_min_eur >= price_max_eur;

SELECT * FROM niches WHERE margin_pct_min <= 0 OR margin_pct_max >= 100 OR margin_pct_min > margin_pct_max;
